import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';

const API_BASE_URL = 'http://localhost:5000/api';

function App() {
  const [isRecording, setIsRecording] = useState(false);
  const [recordedChunks, setRecordedChunks] = useState([]);
  const [recordedVideo, setRecordedVideo] = useState(null);
  const [recordings, setRecordings] = useState([]);
  const [timer, setTimer] = useState(0);
  const [isUploading, setIsUploading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);
  
  const mediaRecorderRef = useRef(null);
  const streamRef = useRef(null);
  const timerRef = useRef(null);

  // Fetch recordings on component mount
  useEffect(() => {
    fetchRecordings();
  }, []);

  // Timer effect
  useEffect(() => {
    if (isRecording && timer < 180) { // 3 minutes max
      timerRef.current = setInterval(() => {
        setTimer(prev => {
          if (prev >= 179) {
            stopRecording();
            return 180;
          }
          return prev + 1;
        });
      }, 1000);
    } else if (!isRecording) {
      if (timerRef.current) {
        clearInterval(timerRef.current);
      }
    }

    return () => {
      if (timerRef.current) {
        clearInterval(timerRef.current);
      }
    };
  }, [isRecording, timer]);

  const startRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getDisplayMedia({
        video: { mediaSource: 'screen' },
        audio: true
      });
      
      streamRef.current = stream;
      const mediaRecorder = new MediaRecorder(stream, {
        mimeType: 'video/webm;codecs=vp9'
      });
      
      mediaRecorderRef.current = mediaRecorder;
      
      const chunks = [];
      mediaRecorder.ondataavailable = (event) => {
        if (event.data.size > 0) {
          chunks.push(event.data);
        }
      };
      
      mediaRecorder.onstop = () => {
        const blob = new Blob(chunks, { type: 'video/webm' });
        setRecordedChunks(chunks);
        setRecordedVideo(blob);
        setRecordedVideo(URL.createObjectURL(blob));
      };
      
      mediaRecorder.start();
      setIsRecording(true);
      setTimer(0);
      setRecordedChunks([]);
      setRecordedVideo(null);
    } catch (error) {
      console.error('Error starting recording:', error);
      alert('Failed to start recording. Please check permissions.');
    }
  };

  const stopRecording = () => {
    if (mediaRecorderRef.current && isRecording) {
      mediaRecorderRef.current.stop();
      if (streamRef.current) {
        streamRef.current.getTracks().forEach(track => track.stop());
      }
      setIsRecording(false);
    }
  };

  const downloadRecording = () => {
    if (recordedVideo) {
      const url = recordedVideo;
      const a = document.createElement('a');
      a.href = url;
      a.download = `screen-recording-${Date.now()}.webm`;
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);
    }
  };

  const uploadRecording = async () => {
    if (!recordedVideo) return;

    try {
      setIsUploading(true);
      setUploadProgress(0);

      const formData = new FormData();
      const blob = await fetch(recordedVideo).then(r => r.blob());
      formData.append('video', blob, `recording-${Date.now()}.webm`);

      const response = await axios.post(`${API_BASE_URL}/recordings`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
        onUploadProgress: (progressEvent) => {
          const progress = Math.round(
            (progressEvent.loaded * 100) / progressEvent.total
          );
          setUploadProgress(progress);
        },
      });

      if (response.data) {
        alert('Recording uploaded successfully!');
        fetchRecordings(); // Refresh the list
        setRecordedVideo(null);
        setRecordedChunks([]);
        setTimer(0);
      }
    } catch (error) {
      console.error('Upload error:', error);
      alert('Failed to upload recording. Please try again.');
    } finally {
      setIsUploading(false);
      setUploadProgress(0);
    }
  };

  const fetchRecordings = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/recordings`);
      setRecordings(response.data);
    } catch (error) {
      console.error('Error fetching recordings:', error);
    }
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  const formatFileSize = (bytes) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleString();
  };

  return (
    <div className="min-h-screen bg-gray-100 py-8">
      <div className="max-w-6xl mx-auto px-4">
        <h1 className="text-4xl font-bold text-center text-gray-800 mb-8">
          MERN Screen Recorder
        </h1>

        {/* Recording Controls */}
        <div className="bg-white rounded-lg shadow-lg p-6 mb-8">
          <div className="flex flex-col items-center space-y-4">
            <div className="text-6xl font-mono text-blue-600">
              {formatTime(timer)}
            </div>
            
            <div className="flex space-x-4">
              {!isRecording ? (
                <button
                  onClick={startRecording}
                  className="bg-green-500 hover:bg-green-600 text-white px-6 py-3 rounded-lg font-semibold transition-colors"
                >
                  Start Recording
                </button>
              ) : (
                <button
                  onClick={stopRecording}
                  className="bg-red-500 hover:bg-red-600 text-white px-6 py-3 rounded-lg font-semibold transition-colors"
                >
                  Stop Recording
                </button>
              )}
            </div>

            {timer >= 180 && (
              <div className="text-red-600 font-semibold">
                Maximum recording time (3 minutes) reached!
              </div>
            )}
          </div>
        </div>

        {/* Recorded Video Preview */}
        {recordedVideo && (
          <div className="bg-white rounded-lg shadow-lg p-6 mb-8">
            <h2 className="text-2xl font-semibold mb-4">Recording Preview</h2>
            <div className="flex flex-col items-center space-y-4">
              <video
                src={recordedVideo}
                controls
                className="max-w-full h-auto rounded-lg shadow-md"
                style={{ maxHeight: '400px' }}
              />
              <div className="flex space-x-4">
                <button
                  onClick={downloadRecording}
                  className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded-lg font-semibold transition-colors"
                >
                  Download
                </button>
                <button
                  onClick={uploadRecording}
                  disabled={isUploading}
                  className="bg-purple-500 hover:bg-purple-600 disabled:bg-gray-400 text-white px-4 py-2 rounded-lg font-semibold transition-colors"
                >
                  {isUploading ? `Uploading... ${uploadProgress}%` : 'Upload to Server'}
                </button>
              </div>
            </div>
          </div>
        )}

        {/* Uploaded Recordings */}
        <div className="bg-white rounded-lg shadow-lg p-6">
          <h2 className="text-2xl font-semibold mb-4">Uploaded Recordings</h2>
          {recordings.length === 0 ? (
            <p className="text-gray-500 text-center py-8">No recordings uploaded yet.</p>
          ) : (
            <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
              {recordings.map((recording) => (
                <div key={recording.id} className="border rounded-lg p-4 hover:shadow-md transition-shadow">
                  <div className="mb-3">
                    <h3 className="font-semibold text-gray-800 truncate">
                      {recording.filename}
                    </h3>
                    <p className="text-sm text-gray-600">
                      Size: {formatFileSize(recording.filesize)}
                    </p>
                    <p className="text-sm text-gray-600">
                      {formatDate(recording.createdAt)}
                    </p>
                  </div>
                  <video
                    src={`${API_BASE_URL}/recordings/${recording.id}`}
                    controls
                    className="w-full h-auto rounded-lg"
                    style={{ maxHeight: '200px' }}
                  />
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
