import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';

const API_URL = 'https://recorder-app-2.onrender.com/api';

function App() {
  // Authentication state
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [username, setUsername] = useState('');
  const [loginError, setLoginError] = useState('');
  
  // State for recording
  const [isRecording, setIsRecording] = useState(false);
  const [recordedVideo, setRecordedVideo] = useState(null);
  const [timer, setTimer] = useState(0);
  
  // State for uploads
  const [recordings, setRecordings] = useState([]);
  const [isUploading, setIsUploading] = useState(false);
  
  // Refs for recording
  const mediaRecorder = useRef(null);
  const stream = useRef(null);
  const timerInterval = useRef(null);

  // Check if user is logged in on app start
  useEffect(() => {
    const savedUser = localStorage.getItem('username');
    if (savedUser) {
      setUsername(savedUser);
      setIsLoggedIn(true);
      loadRecordings();
    }
  }, []);

  // Handle timer
  useEffect(() => {
    if (isRecording) {
      timerInterval.current = setInterval(() => {
        setTimer(prev => {
          if (prev >= 179) { // 3 minutes max
            stopRecording();
            return 180;
          }
          return prev + 1;
        });
      }, 1000);
    } else {
      clearInterval(timerInterval.current);
    }

    return () => clearInterval(timerInterval.current);
  }, [isRecording]);

  // Authentication functions
  const handleLogin = (e) => {
    e.preventDefault();
    const inputUsername = e.target.username.value.trim();
    
    if (!inputUsername) {
      setLoginError('Please enter a username');
      return;
    }
    
    if (inputUsername.length < 3) {
      setLoginError('Username must be at least 3 characters');
      return;
    }
    
    setUsername(inputUsername);
    setIsLoggedIn(true);
    setLoginError('');
    localStorage.setItem('username', inputUsername);
    loadRecordings();
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setUsername('');
    setRecordings([]);
    setRecordedVideo(null);
    setTimer(0);
    localStorage.removeItem('username');
  };

  const startRecording = async () => {
    try {
      // Get screen and audio
      const screenStream = await navigator.mediaDevices.getDisplayMedia({
        video: true,
        audio: true
      });
      
      stream.current = screenStream;
      
      // Create recorder
      const recorder = new MediaRecorder(screenStream, {
        mimeType: 'video/webm'
      });
      
      mediaRecorder.current = recorder;
      
      // Collect video chunks
      const chunks = [];
      recorder.ondataavailable = (event) => {
        if (event.data.size > 0) {
          chunks.push(event.data);
        }
      };
      
      // Create video when recording stops
      recorder.onstop = () => {
        const videoBlob = new Blob(chunks, { type: 'video/webm' });
        setRecordedVideo(URL.createObjectURL(videoBlob));
      };
      
      // Start recording
      recorder.start();
      setIsRecording(true);
      setTimer(0);
      setRecordedVideo(null);
      
    } catch (error) {
      alert('Cannot start recording. Please allow screen access.');
    }
  };

  const stopRecording = () => {
    if (mediaRecorder.current && isRecording) {
      mediaRecorder.current.stop();
      stream.current.getTracks().forEach(track => track.stop());
      setIsRecording(false);
    }
  };

  const downloadRecording = () => {
    if (!recordedVideo) return;
    
    const link = document.createElement('a');
    link.href = recordedVideo;
    link.download = `recording-${Date.now()}.webm`;
    link.click();
  };

  const uploadRecording = async () => {
    if (!recordedVideo) return;

    try {
      setIsUploading(true);

      // Prepare file for upload
      const formData = new FormData();
      const blob = await fetch(recordedVideo).then(r => r.blob());
      formData.append('video', blob, `recording-${Date.now()}.webm`);

      console.log('=== UPLOAD DEBUG INFO ===');
      console.log('File size:', blob.size, 'bytes');
      console.log('File type:', blob.type);
      console.log('API URL:', `${API_URL}/recordings`);
      console.log('FormData entries:');
      for (let [key, value] of formData.entries()) {
        console.log(key, value);
      }

      // Upload to server
      const response = await axios.post(`${API_URL}/recordings`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
        timeout: 60000, // 60 second timeout
        onUploadProgress: (progressEvent) => {
          const percentCompleted = Math.round((progressEvent.loaded * 100) / progressEvent.total);
          console.log(`Upload progress: ${percentCompleted}%`);
        }
      });

      console.log('Upload response:', response.data);
      alert('Recording uploaded successfully!');
      loadRecordings(); // Refresh list
      setRecordedVideo(null);
      setTimer(0);
      
    } catch (error) {
      console.error('=== UPLOAD ERROR ===');
      console.error('Full error:', error);
      console.error('Error message:', error.message);
      console.error('Error code:', error.code);
      console.error('Error response:', error.response?.data);
      console.error('Error status:', error.response?.status);
      console.error('Error headers:', error.response?.headers);
      
      let errorMessage = 'Upload failed. ';
      if (error.response?.status === 413) {
        errorMessage += 'File too large (max 50MB).';
      } else if (error.response?.status === 400) {
        errorMessage += 'Invalid file format or no file sent.';
      } else if (error.code === 'ECONNABORTED') {
        errorMessage += 'Upload timeout (60s).';
      } else if (error.code === 'ERR_NETWORK') {
        errorMessage += 'Network error - check your connection.';
      } else if (error.response?.data?.error) {
        errorMessage += error.response.data.error;
      } else {
        errorMessage += `Error: ${error.message}`;
      }
      
      alert(errorMessage);
    } finally {
      setIsUploading(false);
    }
  };

  const loadRecordings = async () => {
    try {
      console.log('Testing connection to:', API_URL);
      const response = await axios.get(`${API_URL}/recordings`);
      setRecordings(response.data);
      console.log('Successfully loaded recordings:', response.data);
    } catch (error) {
      console.error('Could not load recordings:', error);
      console.error('Error details:', error.response?.data);
    }
  };

  // Helper functions
  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  const formatFileSize = (bytes) => {
    if (bytes === 0) return '0 Bytes';
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(1024));
    return (bytes / Math.pow(1024, i)).toFixed(1) + ' ' + sizes[i];
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString();
  };

  // Login Component
  const LoginForm = () => (
    <div className="min-h-screen bg-gray-100 flex items-center justify-center py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-md w-full space-y-8">
        <div className="text-center">
          <h1 className="text-4xl font-bold text-gray-900 mb-2">Screen Recorder</h1>
          <p className="text-gray-600">Enter your username to start recording</p>
        </div>
        
        <form onSubmit={handleLogin} className="mt-8 space-y-6">
          <div>
            <label htmlFor="username" className="block text-sm font-medium text-gray-700 mb-2">
              Username
            </label>
            <input
              id="username"
              name="username"
              type="text"
              required
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              placeholder="Enter your username"
            />
          </div>
          
          {loginError && (
            <div className="text-red-600 text-sm text-center">{loginError}</div>
          )}
          
          <button
            type="submit"
            className="w-full bg-blue-600 hover:bg-blue-700 text-white font-semibold py-2 px-4 rounded-lg transition-colors"
          >
            Start Recording
          </button>
        </form>
        
        <div className="text-center text-sm text-gray-500">
          <p>No password required - just pick a username!</p>
        </div>
      </div>
    </div>
  );

  // Main App Component
  const MainApp = () => (
    <div className="min-h-screen bg-gray-100 py-8">
      <div className="max-w-4xl mx-auto px-4">
        {/* Header with logout */}
        <div className="flex justify-between items-center mb-8">
          <h1 className="text-3xl font-bold text-gray-800">Screen Recorder</h1>
          <div className="flex items-center gap-4">
            <span className="text-gray-600">Welcome, <span className="font-semibold">{username}</span></span>
            <button
              onClick={handleLogout}
              className="bg-gray-500 hover:bg-gray-600 text-white px-4 py-2 rounded-lg transition-colors"
            >
              Logout
            </button>
          </div>
        </div>

        {/* Recording Section */}
        <div className="bg-white rounded-lg shadow p-6 mb-6">
          <div className="text-center">
            <div className="text-5xl font-mono text-blue-600 mb-4">
              {formatTime(timer)}
            </div>
            
            {!isRecording ? (
              <button
                onClick={startRecording}
                className="bg-green-500 hover:bg-green-600 text-white px-8 py-3 rounded-lg font-semibold"
              >
                Start Recording
              </button>
            ) : (
              <button
                onClick={stopRecording}
                className="bg-red-500 hover:bg-red-600 text-white px-8 py-3 rounded-lg font-semibold"
              >
                Stop Recording
              </button>
            )}

            {timer >= 180 && (
              <p className="text-red-600 mt-2">Maximum time reached!</p>
            )}
            
          </div>
        </div>

        {/* Video Preview */}
        {recordedVideo && (
          <div className="bg-white rounded-lg shadow p-6 mb-6">
            <h2 className="text-xl font-semibold mb-4">Your Recording</h2>
            <video
              src={recordedVideo}
              controls
              className="w-full rounded-lg mb-4"
              style={{ maxHeight: '300px' }}
            />
            <div className="flex gap-4 justify-center">
              <button
                onClick={downloadRecording}
                className="bg-blue-500 hover:bg-blue-600 text-white px-6 py-2 rounded-lg"
              >
                Download
              </button>
              <button
                onClick={uploadRecording}
                disabled={isUploading}
                className="bg-purple-500 hover:bg-purple-600 disabled:bg-gray-400 text-white px-6 py-2 rounded-lg"
              >
                {isUploading ? 'Uploading...' : 'Upload'}
              </button>
            </div>
          </div>
        )}

        {/* Saved Recordings */}
        <div className="bg-white rounded-lg shadow p-6">
          <h2 className="text-xl font-semibold mb-4">Saved Recordings</h2>
          {recordings.length === 0 ? (
            <p className="text-gray-500 text-center py-8">No recordings saved yet.</p>
          ) : (
            <div className="grid gap-4 md:grid-cols-2">
              {recordings.map((recording) => (
                <div key={recording.id} className="border rounded-lg p-4">
                  <div className="mb-3">
                    <h3 className="font-medium text-gray-800">
                      {recording.filename}
                    </h3>
                    <p className="text-sm text-gray-600">
                      {formatFileSize(recording.filesize)} â€¢ {formatDate(recording.createdAt)}
                    </p>
                  </div>
                  <video
                    src={`${API_URL}/recordings/${recording.id}`}
                    controls
                    className="w-full rounded-lg"
                    style={{ maxHeight: '200px' }}
                  />
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );

  // Main return with conditional rendering
  return isLoggedIn ? <MainApp /> : <LoginForm />;
}

export default App;
